package Geeks;

import java.util.HashMap;
import java.util.Map;

public class MaxDecodeString {

    public static int countDecodings(String s) {
        Map<Integer, Integer> memo = new HashMap<>();
        return decodeHelper(s, 0, memo);
    }

    private static int decodeHelper(String s, int index, Map<Integer, Integer> memo) {
        // Base case: If we've reached the end of the string, return 1 (one valid decoding)
        if (index == s.length()) {
            return 1;
        }

        // If the string starts with '0', return 0 (invalid decoding)
        if (s.charAt(index) == '0') {
            return 0;
        }

        // If this subproblem has already been solved, return the stored result
        if (memo.containsKey(index)) {
            return memo.get(index);
        }

        // Decode one digit and recursively decode the rest
        int result = decodeHelper(s, index + 1, memo);

        // Decode two digits (if valid) and recursively decode the rest
        if (index + 1 < s.length()) {
            int twoDigit = Integer.parseInt(s.substring(index, index + 2));
            if (twoDigit >= 10 && twoDigit <= 26) {
                result += decodeHelper(s, index + 2, memo);
            }
        }

        result %= 1000000007;
        // Store the result in the memo map
        memo.put(index, result);

        return result;
    }

    public static void main(String[] args) {
        String s = "";
        System.out.println("Number of decodings for " + s + ": " + countDecodings(s));  // Output: 3
    }
}
